        jEditorPane3.setEditable(false);
        jEditorPane3.setBackground(new java.awt.Color(204, 255, 204));
        jEditorPane3.setContentType("text/html"); // NOI18N
        jEditorPane3.setText("<h3 style=\"text-align: justify;\"><span style=\"color: #33cccc;\"><strong>&rArr; <span style=\"text-decoration: underline;\">Formato de fichero de aut&oacute;mata</span> &lArr;</strong></span></h3> <p><span style=\"color: #000000;\">A continuaci&oacute;n se muestra un ejemplo del lenguaje de descripci&oacute;n de aut&oacute;matas creado para esta pr&aacute;ctica.</span></p> <hr /> <p style=\"text-align: justify; padding-left: 30px;\">ESTADOS: 0 1 2 3 4 5 6</p> <p style=\"text-align: justify; padding-left: 30px;\">INICIAL: 0</p> <p style=\"text-align: justify; padding-left: 30px;\">FINALES: 4</p> <p style=\"text-align: justify; padding-left: 30px;\">TRANSICIONES:</p> <p style=\"text-align: justify; padding-left: 30px;\">0 '1' [1]</p> <p style=\"text-align: justify; padding-left: 30px;\">3 '2' [5]</p> <p style=\"text-align: justify; padding-left: 30px;\">2 '2' [4]</p> <p style=\"text-align: justify; padding-left: 30px;\">6 '3' [4]</p> <p style=\"text-align: justify; padding-left: 30px;\">4 '0' [0]</p> <p style=\"text-align: justify; padding-left: 30px;\">1 'lambda' [2, 3]</p> <p style=\"text-align: justify; padding-left: 30px;\">5 'lambda' [6]</p> <p style=\"text-align: justify; padding-left: 30px;\">0 'lambda' [1]</p> <p style=\"text-align: justify; padding-left: 30px;\">FIN</p> <hr /> <p style=\"text-align: justify;\">El&nbsp;aut&oacute;mata&nbsp;descrito por el c&oacute;digo anterior generar&iacute;a el siguiente grafo:</p> <p style=\"text-align: justify;\"><img src=\"https://i.imgur.com/sIdLpZG.png\" width=\"600\" height=\"116\" /></p> <hr /> <p style=\"text-align: justify;\"><span style=\"background-color: #ffff00;\">La extensi&oacute;n del fichero del aut&oacute;mata deber&aacute; de ser \"<em><strong>.auto</strong></em>\"</span></p>");
        jEditorPane3.setToolTipText("");
